import * as React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { Image } from 'react-native';
import Ionicons from '@expo/vector-icons/Ionicons';



function ListScreen() {

    const Row = ({ children }) => (
        <View style={styles.row}>{children}</View>
    )

    const Col = ({ numRows, children }) => {
        return (
            <View style={styles[`${numRows}col`]}>{children}</View>
        )
    }


    return (
        <View>
            <Text style={styles.title}>
                <Ionicons name={'map'} size={30} color={'#fff'} />
                Localiza URI
            </Text>

            <View style={styles.container}>
                <Row>
                    <Col numRows={1}>
                        <Image style={styles.img} source={{ uri: "https://www.imagemhost.com.br/images/2022/11/21/adm_20.png" }} />
                        <Text>Predio 20 local do curso de Adimistração</Text>
                    </Col>

                </Row>

            </View>

        </View>
    );
}

const styles = StyleSheet.create({
    title:
    {
        fontSize: 30,
        marginTop: 20,
        padding: 10,
        height: 60,
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        borderBottomWidth: 2,
        borderBottomColor: 'solid rgb(73, 88, 221)',
        width: '100%',
        fontWeight: '800',
        textAlign: 'center',
        color: '#fff'
    },
    container:
    {
        marginTop:100,
        flex: 2,
        marginHorizontal: "auto",
        width: 400,
        backgroundColor: "red"
    },

    row: {
        flexDirection: "row"
    },
    "1col": {
        backgroundColor: "lightblue",
        borderColor: "#fff",
        borderWidth: 1,
        color: 'black',
        flex: 1
    },
    "2col": {
        backgroundColor: "green",
        borderColor: "#fff",
        borderWidth: 1,
        color: 'black',
        flex: 2
    },
});


export default ListScreen;
